import { useEffect, useState } from 'react';
import { getMenus } from '../api';
import './MenuOptionBoth.css';

function MenuOptionBoth({ id, setOption }) {
  const [menus, setMenus] = useState([]);
  const [count, setCount] = useState(1);
  const [totalPrice, setTotalPrice] = useState(0);

  useEffect(() => {
    const fetchMenus = async () => {
      const menuData = await getMenus();
      setMenus(menuData);
    };

    fetchMenus();
  }, []);

  const menu = menus[id - 1];

  let price;
  if (menu) {
    price = Math.min(menu.price_hot, menu.price_ice);
  }

  let price_same;
  if (menu) {
    if (menu.price_hot === menu.price_ice) {
      price_same = 1;
    }
  }

  if (price) {
    setTotalPrice(price);
  }

  const closeOption = () => {
    setOption(false);
  };

  const plusButton = () => {
    setCount((prevCount) => prevCount + 1);
  };

  const minusButton = () => {
    if (count > 1) {
      setCount((prevCount) => prevCount - 1);
    }
  };

  return (
    <div className="option-container">
      {menu ? (
        <>
          <div className="top-menu-option">
            <img
              className="pic"
              src={`${process.env.PUBLIC_URL}/Imgs/아메리카노.png`}
              alt={menu.name}
            />
            <div className="basic-menu">
              <p className="menu-name" id="name">
                {menu.name}
              </p>
              <p className="menu-price">￦ {price}</p>
              <div className="count-container">
                <button className="count-button" onClick={minusButton}>
                  -
                </button>
                <span className="count">{count}</span>
                <button className="count-button plus" onClick={plusButton}>
                  +
                </button>
              </div>
            </div>
          </div>
          <p className="option-box">메뉴 설명</p>
          <hr />
          <p id="menu-description">{menu.description}</p>
          <p className="option-box">무료 옵션</p>
          <hr />
          {price_same ? (
            <div id="temp-option">
              <span className="temp">온도</span>
              <button className="choose">
                <img
                  src={`${process.env.PUBLIC_URL}/Imgs/따뜻하게.png`}
                  alt="따뜻하게"
                />
                <p>따뜻하게</p>
              </button>
              <button className="choose">
                <img
                  src={`${process.env.PUBLIC_URL}/Imgs/시원하게.png`}
                  alt="시원하게"
                />
                <p>시원하게</p>
              </button>
            </div>
          ) : null}
          <div id="cup-option">
            <span>컵 선택</span>
            <button className="choose">
              <p>일회용기</p>
              <img
                src={`${process.env.PUBLIC_URL}/imgs/일회용기.png`}
                alt="일회용기"
              />
              <p className="cup-small-option">매장 이용 불가</p>
            </button>
            <button className="choose">
              <p>매장용컵</p>
              <img
                src={`${process.env.PUBLIC_URL}/imgs/매장용컵.png`}
                alt="매장용컵"
              />
              <p className="cup-small-option">매장 이용</p>
            </button>
            <button className="choose">
              <p>텀블러</p>
              <img
                src={`${process.env.PUBLIC_URL}/imgs/텀블러.png`}
                alt="텀블러"
              />
              <p className="cup-small-option">스탬프 적립</p>
            </button>
          </div>
          <p className="option-box">유료 옵션</p>
          <hr />
          {!price_same ? (
            <div id="temp-option">
              <span className="temp">온도</span>
              <button className="choose">
                <img
                  src={`${process.env.PUBLIC_URL}/Imgs/따뜻하게.png`}
                  alt="따뜻하게"
                />
                <p>따뜻하게</p>
              </button>
              <button className="choose">
                <img
                  src={`${process.env.PUBLIC_URL}/Imgs/시원하게.png`}
                  alt="시원하게"
                />
                <p>시원하게</p>
              </button>
            </div>
          ) : null}
          <hr id="total-line" />
          <div>
            <span>총 금액</span>
            <span>(제품가격+옵션가격)</span>
            <span>￦ ${totalPrice}</span>
          </div>
        </>
      ) : null}
      <button onClick={closeOption}>닫기</button>
    </div>
  );
}

export default MenuOptionBoth;
