import { useState } from 'react';

function TempOption({ price }) {
  const [activeTemp, setActiveTemp] = useState(null);
  const [isTempClicked, setIsTempClicked] = useState(null);

  if (price) {
    const { ice, hot } = price;
  }

  const handleTempClick = (e) => {
    const clickedValue = e.target.value;
    setIsTempClicked(clickedValue);
    if (!price[value]) return;
    setActiveTemp(value === activeTemp ? null : value);
  };

  return (
    <div id="temp-option">
      <span className="temp">온도</span>
      <button
        value="따뜻하게"
        className={`choose ${isTempClicked === '따뜻하게' ? 'active' : ''}`}
        onClick={handleTempClick}
      >
        <img
          src={`${process.env.PUBLIC_URL}/Imgs/따뜻하게.png`}
          alt="따뜻하게"
        />
        <p>따뜻하게</p>
      </button>
      <button
        value="시원하게"
        className={`choose ${isTempClicked === '시원하게' ? 'active' : ''}`}
        onClick={handleTempClick}
      >
        <img
          src={`${process.env.PUBLIC_URL}/Imgs/시원하게.png`}
          alt="시원하게"
        />
        <p>시원하게</p>
      </button>
    </div>
  );
}

export default TempOption;
