// App.js
import React, { useState } from 'react';
import BackButton from './BackButton';
import './App.css';
import Call from './Call';
import Pay from './Pay';
import MenuOptionBoth from './MenuOptionBoth';
import Test from './test';

function App() {
  const [modal, setModal] = useState(false);
  const [selectedNum, setSelectedNum] = useState(null);

  const showModal = (num) => {
    setSelectedNum(num);
    setModal(true);
  };

  const pk = 3;

  return (
    <div className="App">
      <MenuOptionBoth id={3} />
      <div className="nav">
        <BackButton />
        <img
          src={`${process.env.PUBLIC_URL}/Imgs/logo.png`}
          alt="카페 로고"
          className="logo"
        />
      </div>
      <h1 className="payment">결제하기</h1>
      <div className="paymentRow">
        <div>
          <Payment
            method={`${process.env.PUBLIC_URL}/Imgs/card.svg`}
            imgSrc={card}
            showModal={() => showModal(1)}
          />
        </div>
        <div>
          <Payment
            method={'기프티콘'}
            imgSrc={gifticon}
            showModal={() => showModal(2)}
          />
        </div>
      </div>
      <div className="paymentRow">
        <div>
          <Payment
            method={'선불 카드'}
            imgSrc={pre_card}
            showModal={() => showModal(2)}
          />
        </div>
        <div>
          <Payment
            method={'카카오페이'}
            imgSrc={kakaopay}
            showModal={() => showModal(2)}
          />
        </div>
      </div>
      <div className="smallButton">
        <Call showModal={() => showModal(3)} />
      </div>
      {modal && <Pay setModal={setModal} num={selectedNum} />}
    </div>
  );
}

export default App;
