      <span className="cup-size">컵 사이즈</span>
      <button
        value="small"
        className={`choose ${isCupSizeClicked === 'small' ? 'active' : ''}`}
        onClick={() => handleTempButtonClick('hot')}
        disabled={price.hot === 0}
      >
        <img
          src={`${process.env.PUBLIC_URL}/Imgs/따뜻하게.png`}
          alt="따뜻하게"
        />
        <p>
          따뜻하게{' '}
          {price.hot > 0 &&
          price.ice > 0 &&
          price.hot !== price.ice &&
          price.hot > price.ice
            ? `(+${Math.abs(price.hot - price.ice)}원)`
            : null}
        </p>
      </button>
      <button
        value="ice"
        className={`choose ${price.ice === 0 ? 'disabled' : ''} ${
          isTempClicked === 'ice' ? 'active' : ''
        }`}
        onClick={() => handleTempButtonClick('ice')}
        disabled={price.ice === 0}
      >
        <img
          src={`${process.env.PUBLIC_URL}/Imgs/시원하게.png`}
          alt="시원하게"
        />
        <p>
          시원하게{' '}
          {price.hot > 0 &&
          price.ice > 0 &&
          price.hot !== price.ice &&
          price.ice &&
          price.ice > price.hot
            ? `(+${Math.abs(price.hot - price.ice)}원)`
