// App.js
import React, { useState } from 'react';
import BackButton from './BackButton';
import './App.css';
import logo from '../imgs/logo.png';
import card from '../imgs/card.svg';
import gifticon from '../imgs/Vector.svg';
import pre_card from '../imgs/선불카드 2.svg';
import kakaopay from '../imgs/카카오페이 1.svg';
import Payment from './Payment';
import Call from './Call';
import { useOrder } from './orderContext';
import Pay from './Pay';

function App() {
  const [orderNum, incrementOrderNum] = useOrder();
  const [modal, setModal] = useState(false);

  const showModal = () => {
    setModal(true);
  };

  return (
    <div className="App">
      <div className="nav">
        <BackButton />
        <img src={logo} alt="카페 로고" className="logo" />
      </div>
      <h1 className="payment">결제하기</h1>
      <div className="paymentRow">
        <div>
          <Payment method={'카드 결제'} imgSrc={card} showModal={showModal} />
          {modal && <Pay setModal={setModal} num={1} />}
        </div>
        <div>
          <Payment
            method={'기프티콘'}
            imgSrc={gifticon}
            showModal={showModal}
          />
          {modal && <Pay setModal={setModal} num={2} />}
        </div>
      </div>
      <div className="paymentRow">
        <div>
          <Payment
            method={'선불 카드'}
            imgSrc={pre_card}
            showModal={showModal}
          />
          {modal && <Pay setModal={setModal} num={2} />}
        </div>
        <div>
          <Payment
            method={'카카오페이'}
            imgSrc={kakaopay}
            showModal={showModal}
          />
          {modal && <Pay setModal={setModal} num={2} />}
        </div>
      </div>
      <div className="smallButton">
        <Call showModal={showModal} />
        {modal && <Pay setModal={setModal} num={3} />}
      </div>
    </div>
  );
}

export default App;
