import { useEffect, useState } from 'react';
import { getMenus } from '../api';
import './MenuOptionBoth.css';

function MenuOptionBoth({ id, setOption }) {
  const [menus, setMenus] = useState([]);
  const [count, setCount] = useState(1);

  const plusButton = () => {
    setCount((prevCount) => prevCount + 1);
    console.log(count);
  };

  const minusButton = () => {
    if (count > 1) {
      setCount((prevCount) => prevCount - 1);
    }
  };

  useEffect(() => {
    const fetchMenus = async () => {
      const menuData = await getMenus();
      setMenus(menuData);
    };

    fetchMenus();
  }, []);

  console.log(menus);

  const menu = menus[id - 1];
  console.log(menu);

  let price;
  if (menu) {
    price = Math.min(menu.price_hot, menu.price_ice);
  }

  return (
    <div className="option-container">
      {menu ? (
        <>
          <div className="top-menu-option">
            <img
              className="pic"
              src={`${process.env.PUBLIC_URL}/Imgs/아메리카노.png`}
              alt={menu.name}
            />
            <div className="basic-menu">
              <p className="menu-name" id="name">
                {menu.name}
              </p>
              <p className="menu-price">{price}</p>
              <div className="count-container">
                <button className="count-button" onClick={minusButton}>
                  -
                </button>
                <span className="count">{count}</span>
                <button className="count-button plus" onClick={plusButton}>
                  +
                </button>
              </div>
            </div>
          </div>
          <p>메뉴 설명</p>
          <p>{menu.description}</p>
          <p>무료 옵션</p>
          <div>
            <span>컵 선택</span>
            <div>
              <p>일회용기</p>
              <img
                src={`${process.env.PUBLIC_URL}/imgs/일회용기.png`}
                alt="일회용기"
              />
              <p>매장 이용 불가</p>
            </div>
            <div>
              <p>매장용컵</p>
              <img
                src={`${process.env.PUBLIC_URL}/imgs/매장용컵.png`}
                alt="매장용컵"
              />
              <p>매장 이용</p>
            </div>
            <div>
              <p>텀블러</p>
              <img
                src={`${process.env.PUBLIC_URL}/imgs/텀블러.png`}
                alt="텀블러"
              />
              <p>스탬프 적립</p>
            </div>
          </div>
        </>
      ) : null}
      <button onClick={setOption}>닫기</button>
    </div>
  );
}

export default MenuOptionBoth;
